          <p>{data.product.brand}</p>
        </div>

        <div className={cart.ProductAttributes}>
          {data.product.attributes.map(attribute => <ProductAttribute key={attribute.name} attribute={attribute} setSelectedAttributes={setSelectedAttributes} />)}
        </div>
      </div>
    </div>
  )
  }







