import { gql, useQuery } from "@apollo/client";
// import gql from "graphql-tag";
import React, { useEffect, useState } from "react";
import Cart from "./components/cart";
import Navbar from "./components/navbar";
import Prodcut from "./components/product";

const getCategories = gql`
  query {
    categories {
      name

      products {
        name
        gallery
        prices {
          currency {
            label
          }
          amount
        }
      }
    }
  }
`;


function App() {
  const [activeIndex, setActiveIndex] = useState(0);
  const { data } = useQuery(getCategories);

  return (
    <div>
      <Navbar
        items={data?.categories}
        active={activeIndex}
        setActive={setActiveIndex} />

     <Prodcut
       category={data?.categories[activeIndex]?.name}
       products={
           data?.categories.filter((c) => c.name === activeIndex)[0]?.products
          // data?.categories[activeIndex]?.products

          }
      />


    </div>
 //   <div className="App">


   //     <Cart></Cart>


   //   </div>
  );
}

export default App;

