import React from 'react'
import { gql, useQuery } from '@apollo/client'

import cart from './cart.module.css'
import { useState } from 'react'

const selectedProduct = gql`
query {
  product (id:"ps-5") {
    name
    brand
    attributes {
        name
        type
        items {
          value
          displayValue
        }
      }
    gallery
    
  }
}
`

export const Cart = () => {
    const [selectedAttributes, setSelectedAttributes] = useState({})
  
    const { data, loading } = useQuery(selectedProduct)
  
    console.log(selectedAttributes)
  
    if (loading) return <h1>Loading ...</h1>
  
    return (
      <div>
        

        <div className={cart.cartHeader}>
        <div><strong>CART</strong></div>
        <p style={{width: '25px', height: '25px'}}></p>
      
        </div>
 
  
        <div className={cart.ProductData}>
          <div className={cart.ProductHeader}>
            <h1>{data.product.name}</h1>
            <p>{data.product.brand}</p>
          </div>
          
          <div className={cart.ProductAttributes}>
            {data.product.attributes.map(attribute => <ProductAttribute key={attribute.name} attribute={attribute} setSelectedAttributes={setSelectedAttributes} />)}
          </div>
        
         </div> 
        <div className={cart.ProductImage}>
            <img src={data.product.gallery[0]} alt='product preview large' />
        </div>


     </div>   
    )
  }



export  const ProductAttribute = (props) => {
  const { attribute, setSelectedAttributes } = props
  const updateAttribute = (item) => setSelectedAttributes((prevState) => ({...prevState, [attribute.name]: item.value}))

  return <div className={cart.ProductAttribute}>
    <p><strong>{attribute.name}</strong></p>
    <div className={cart.ProductAttributeItems}>
      {attribute.items.map(item => 
        <p style={attribute.type === 'swatch' ? {backgroundColor: item.value, width: '25px', height: '25px'} : {}} onClick={() => updateAttribute(item)}>{attribute.type !== 'swatch' && item.displayValue}</p>
      )}
    </div>
  </div>
}

export default Cart;
