import { gql, useQuery } from '@apollo/client'
import React from 'react'

const selectedProduct = gql`
query {
  product (id:"huarache-x-stussy-le") {
    name
    brand
    description
    gallery
    
  }
}
`

export const Product = () => {
    const { data, loading } = useQuery(selectedProduct)

    
    if (loading) return <h1>Loading ...</h1>

    return (
        <div>
            <h1>{data.product.name}</h1>
            <p>{data.product.brand}</p>
            <img src={data.product.gallery[0]} alt='product preview' />
            <img src={data.product.gallery[1]} alt='product preview' />
            <img src={data.product.gallery[2]} alt='product preview' />
            <img src={data.product.gallery[3]} alt='product preview' />
            <img src={data.product.gallery[4]} alt='product preview' />
        </div>
    )
}
