import { gql, useQuery } from "@apollo/client";
// import gql from "graphql-tag";
import React, { useEffect, useState } from "react";
import Navbar from "./components/navbar";
import Prodcut from "./components/product";

const getCategories = gql`
  query {
    categories {
      name

      products {
        name
        gallery
        prices {
          currency {
            label
          }
          amount
        }
      }
    }
  }
`;

/**
 *  constructor() {
 *    this.state ={
 *      active: null
 *    }
 * }
 */

function App() {
  const [activeIndex, setActiveIndex] = useState(0);
  const { data } = useQuery(getCategories);

  console.log({data})
  console.log({ activeIndex });
  // console.log({ data });
  // console.log(data?.categories);
  // console.log(data?.categories.filter((c) => c.name === active));

  // useEffect(() => {
  //   setActiveIndex(0);
  // }, [data]);

  return (
    <div>
      <Navbar
        items={data?.categories}
        active={activeIndex}
        setActive={setActiveIndex}
      />
      <Prodcut
        category={data?.categories[activeIndex]?.name}
        products={
          // data?.categories.filter((c) => c.name === activeIndex)[0]?.products
          data?.categories[activeIndex]?.products
        }
      />
    </div>
  );
}

export default App;
